    private void searchButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchButtonActionPerformed
        // initialize a boolean variable to check if the email field is empty or not
    boolean check=false;
    // retrieve the email from the email field in the UI
    email=tfEmail.getText();
    // check if the email field is empty
    if(email.equals("")){
        check=true;
        JOptionPane.showMessageDialog(null, "Email Field is Required");
    }
    // if the email field is not empty, check if it is a valid email address
    else if (!isValidEmail(email)){
        // if it is not a valid email address, display an error message and clear the email field
        JOptionPane.showMessageDialog(null, "Invalid email address", "Error", JOptionPane.ERROR_MESSAGE);
        tfEmail.setText("");
    }
    else{
        // if the email address is valid, retrieve the corresponding record from the database
        ResultSet rs=Select.getData("select * from employees where emailEmp = '"+email+"'");
        try{
            if (rs.next())
            {
                // if a record is found, set the check variable to true
                check=true;
                // set the security question field in the UI to the corresponding security question from the record
                tfSecurityQuestion.setEditable(false);
                tfEmail.setEditable(false);
                tfSecurityQuestion.setText(rs.getString(8));
            }
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }
    // if the check variable is false, it means that the email does not exist in the database
    if (!check){
        JOptionPane.showMessageDialog(null, "This Email Does Not Exist");
    }
    }//GEN-LAST:event_searchButtonActionPerformed
public static boolean isValidEmail(String email) {
    if (email == null || email.isEmpty()) {
        return false;
    }
    int atPos = email.indexOf('@');
    if (atPos < 1 || atPos != email.lastIndexOf('@')) {
        return false;
    }
    String domain = email.substring(atPos + 1);
    if (domain.isEmpty() || domain.indexOf('.') < 1 || domain.lastIndexOf('.') == domain.length() - 1) {
        return false;
    }
    return true;
}
