    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveButtonActionPerformed
         boolean check=false; // initialize a boolean variable as false
        String SecurityQuestion=tfSecurityQuestion.getText(); // get the security question from the text field
        String answer=tfAnswer.getText(); // get the answer to the security question from the text field
        String newpassword=pfPassword.getText(); // get the new password from the password field

        if(answer.equals("")||newpassword.equals("")){ // check if either the answer or the new password fields are empty
            check=true; // set the check variable to true if either field is empty
            JOptionPane.showMessageDialog(null, "All Fields are required"); // show a message dialog indicating that all fields are required
        }
        else{ // if both fields are filled
            ResultSet rs =Select.getData("select * from employees where emailEmp = '"+email+"' and SecurityQuestion='"+SecurityQuestion+"' and answer= '"+answer+"'");
            // execute a SQL query to check if the security question and answer provided are correct for the given email
            try {
                if(rs.next()){ // if the query returns a row, the answer to the security question is correct
                    check=true; // set the check variable to true
                    employeeDB.updateEmp("update employees set password= '"+newpassword+"' where emailEmp ='"+email+"'" , "Password Set Successfully");
                    // execute a SQL query to update the password for the given email
                    dispose(); // close the current window
                    new Login().setVisible(true); // open the login window
                }
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null, e); // show an error message if an exception occurs
            }
        }
        if(!check){ // if the check variable is still false, the answer to the security question is incorrect
            JOptionPane.showMessageDialog(null, "Incorrect Answer"); // show an error message indicating that the answer is incorrect
        }
    }//GEN-LAST:event_saveButtonActionPerformed

    private void tfEmailFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_tfEmailFocusLost
        String email = tfEmail.getText();
        if (!isValidEmail(email)) {
            JOptionPane.showMessageDialog(null, "Invalid email address", "Error", JOptionPane.ERROR_MESSAGE);
            tfEmail.setText("");
        }
    }//GEN-LAST:event_tfEmailFocusLost

